import React from "react";
import styles from "../css/Card.module.css";

// 로그인 상태 확인
const isLoggedIn = () => {
  return !!localStorage.getItem("token"); // 토큰이 있으면 true, 없으면 false 반환
};

const Card = (props) => {
  if (!isLoggedIn) {
    return (
      <div
        className={`${styles.card} ${
          props.id === "login" ? styles.nonBlurred : styles.blurred
        }`}
      >
        {props.children}
      </div>
    );
  } else {
    return (
      <div className={`${styles.card} ${styles.nonBlurred}`}>
        {props.children}
      </div>
    );
  }
};

export default Card;
