import React from "react";
import styles from "../css/Card.module.css";

// 로그인 상태 확인
const isLoggedIn = () => {
  return !!localStorage.getItem("token"); // 토큰이 있으면 true, 없으면 false 반환
};

console.log(isLoggedIn);
const Card = (props) => {
  if (isLoggedIn === false) {
    return (
      <div className={`${styles.card} ${styles.blurred}`}>{props.children}</div>
    );
  }
  return (
    <div className={`${styles.card} ${styles.nonBlurred}`}>
      {props.children}
    </div>
  );
};

export default Card;
