import React, { useEffect, useState } from "react";
import styles from "../css/Card.module.css";
import axios from "axios";

const Card = (props) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      checkTokenValidity();
    }, 5000); // 5분마다 토큰 유효성 검사, 실제로는 300000ms 사용 권장

    return () => clearInterval(interval);
  }, []);

  const checkTokenValidity = async () => {
    try {
      const response = await axios.get("/verify-token", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });
      console.log(response.data.message); // "Token is valid"
      setIsLoggedIn(true);
    } catch (error) {
      console.error("Token validation error", error.response);
      setIsLoggedIn(false);
      localStorage.removeItem("token"); // 로그인 상태가 유효하지 않으면 토큰 제거
    }
  };
  console.log(props.id);
  return (
    <div
      className={`${styles.card} ${
        isLoggedIn && props.id !== "login" ? styles.nonBlurred : styles.blurred
      }`}
    >
      {props.children}
    </div>
  );
};

export default Card;
